int main ( ) { int q , t ; scanf ( "%d" , & t ) ; for ( q = 0 ; q < t ; q ++ ) { long int a , b , i , c , d ; scanf ( "%ld%ld" , & a , & b ) ; if ( a > b ) { for ( i = b ; i >= 1 ; i -- ) { if ( a % i == 0 && b % i == 0 ) { c = i ; break ; } } } else { for ( i = a ; i >= 1 ; i -- ) { if ( a % i == 0 && b % i == 0 ) { c = i ; break ; } } } d = ( a * b ) / c ; printf ( "%ld %ld\n" , c , d ) ; } return 0 ; }