int main ( ) { int a , b , gcd , lcm , temp , numerator , denominator , t ; scanf ( "%d" , & t ) ; if ( t >= 1 && t <= 1000 ) { while ( t -- ) { scanf ( "%ld %ld" , & a , & b ) ; if ( a >= 1 && a <= 1000000 && b >= 1 && b <= 1000000 ) { if ( a > b ) { numerator = a ; denominator = b ; } else { numerator = b ; denominator = a ; } while ( denominator != 0 ) { temp = denominator ; denominator = numerator % denominator ; numerator = temp ; } gcd = numerator ; lcm = ( a * b ) / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } } } }