int gcd ( a , b ) { if ( a % b == 0 ) return b ; if ( b % a == 0 ) return a ; if ( a > b ) return gcd ( b , a % b ) ; if ( b > a ) return gcd ( a , b % a ) ; } int lcm ( a , b ) { if ( a % b == 0 ) return a ; if ( b % a == 0 ) return b ; if ( a % b != 0 && b % a != 0 ) return ( a * b ) ; } main ( ) { int t , a , b ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d\n" , & a , & b ) ; printf ( "%d %d\n" , gcd ( a , b ) , lcm ( a , b ) ) ; } }