int main ( ) { register int i ; int t ; long a , b , x , j ; scanf ( "%lld" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lld %lld" , & a , & b ) ; if ( b >= a ) { int j = 2 ; x = b ; while ( x % a != 0 ) { x = b * j ; j ++ ; } printf ( "%lld %lld\n" , ( a * b ) / x , x ) ; } if ( a > b ) { int j = 2 ; x = a ; while ( x % b != 0 ) { x = a * j ; j ++ ; } printf ( "%lld %lld\n" , ( a * b ) / x , x ) ; } } return 0 ; }