int main ( void ) { long t , a , b , hcf , lcm , n , i ; scanf ( "%ld" , & t ) ; while ( t > 0 ) { scanf ( "%ld%ld" , & a , & b ) ; if ( a > b ) { n = b ; } else { n = a ; } for ( i = 1 ; i <= n ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { hcf = i ; } lcm = ( a * b ) / hcf ; } printf ( "%ld %ld\n" , hcf , lcm ) ; t -- ; } return 0 ; }