int main ( void ) { int a [ 100000 ] , b [ 100000 ] , i , count , t , n ; scanf ( "%d" , & t ) ; while ( t -- ) { count = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; b [ 0 ] = 1 ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ i ] >= a [ i - 1 ] ) b [ i ] = b [ i - 1 ] + 1 ; if ( a [ i - 1 ] > a [ i ] ) b [ i ] = 1 ; } for ( i = 0 ; i < n ; i ++ ) count += b [ i ] ; printf ( "%d\n" , count ) ; } return 0 ; }