int main ( void ) { int i , j , k , A , B , T , gcd , lcm = 0 , min , max ; scanf ( "%d" , & T ) ; if ( T >= 1 && T <= 1000 ) { for ( i = 1 ; i <= T ; i ++ ) { scanf ( "%d%d" , & A , & B ) ; if ( A >= 1 && A <= 1000000 && B >= 1 && B <= 1000000 ) { if ( A < B ) min = A ; else min = B ; for ( j = 1 , gcd = 0 ; j <= min ; j ++ ) { if ( A % j == 0 && B % j == 0 ) { if ( j > gcd ) gcd = j ; else gcd = gcd ; } } if ( A > B ) max = A ; else max = B ; for ( k = max ; k <= ( A * B ) ; k ++ ) { if ( k % A == 0 && k % B == 0 ) { lcm = k ; break ; } } printf ( "%d     %d\n" , gcd , lcm ) ; } } } return 0 ; }