int gcd ( int , int ) ; int main ( ) { int t , a , b , r = 0 ; long l = 0 ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; r = gcd ( a , b ) ; l = a / r * b ; printf ( "%d %ld\n" , r , l ) ; } return 0 ; } int gcd ( int i1 , int i2 ) { int a , b ; if ( i1 > i2 ) { a = i1 ; b = i2 ; } else { a = i2 ; b = i1 ; } int result ; int c = a % b ; if ( c > 0 ) result = gcd ( b , c ) ; else result = b ; return result ; }