int main ( ) { int i , t ; long int n1 , n2 , lcm , gcd , num1 , num2 ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%ld %ld" , & n1 , & n2 ) ; if ( n1 < 0 ) n1 = n1 * ( - 1 ) ; if ( n2 < 0 ) n2 = n2 * ( - 1 ) ; num1 = n1 ; num2 = n2 ; while ( n1 != n2 ) { if ( n1 > n2 ) n1 = n1 - n2 ; else n2 = n2 - n1 ; } gcd = n1 ; lcm = ( num1 * num2 ) / gcd ; printf ( "%ld %ld\n" , gcd , lcm ) ; } return 0 ; }