int swap ( long long int * a , long long int * b ) { long long int t = * a ; * a = * b ; * b = t ; } int main ( void ) { int T ; scanf ( "%d" , & T ) ; while ( T -- ) { long long int N , j ; scanf ( "%lld" , & N ) ; long long int a [ N ] , sum = 0 , i , k , l , p = N - 1 ; for ( i = 0 ; i < N ; i ++ ) { scanf ( "%lld" , & a [ i ] ) ; } for ( j = 0 ; j < p ; j ++ ) { if ( a [ 0 ] <= a [ 1 ] ) { for ( k = 1 ; k < N - 1 ; k ++ ) { a [ k ] = a [ k + 1 ] ; } sum = sum + a [ 0 ] ; N -- ; } else if ( a [ 0 ] > a [ 1 ] ) { swap ( & a [ 0 ] , & a [ 1 ] ) ; for ( l = 1 ; l < N - 1 ; l ++ ) { a [ l ] = a [ l + 1 ] ; } sum = sum + a [ 0 ] ; N -- ; } } printf ( "%lld\n" , sum ) ; } return 0 ; }