int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int n1 , n2 , gcd = 1 ; long long int lcm ; scanf ( "%lld%lld" , & n1 , & n2 ) ; long long int i ; if ( n1 == n2 ) { gcd = n1 ; lcm = n1 ; } else if ( n1 > n2 ) { for ( i = 1 ; i <= n2 ; i ++ ) { if ( n1 % i == 0 && n2 % i == 0 ) gcd = i ; } lcm = n1 * n2 / gcd ; } else { for ( i = 1 ; i <= n1 ; i ++ ) { if ( n1 % i == 0 && n2 % i == 0 ) gcd = i ; } lcm = n1 * n2 / gcd ; } printf ( "%lld %lld\n" , gcd , lcm ) ; } }