int main ( ) { int T ; scanf ( "%d" , & T ) ; if ( T >= 1 && T <= 1000 ) { int a = 1 ; for ( a > 0 ; a <= T ; a ++ ) { int A , B ; int lcm , gcd ; int rem , num , den ; scanf ( "%d %d" , & A , & B ) ; if ( A > B ) { num = A ; den = B ; } else { num = B ; den = A ; } rem = num % den ; while ( rem != 0 ) { num = den ; den = rem ; rem = num % den ; } gcd = den ; lcm = A * B / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } } }