int main ( ) { long int a [ 100000 ] ; int t , i , j , k , count , n ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { count = 0 ; scanf ( "%d" , & n ) ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%ld" , & a [ j ] ) ; for ( j = 0 ; j < n ; j ++ ) { for ( k = j ; k < n ; k ++ ) { if ( a [ k ] < a [ k + 1 ] ) { count ++ ; } else { count ++ ; break ; } } } printf ( "\n%d" , count ) ; } }