int check ( long int a [ ] , long int i , long int k ) { int j , count = 0 ; for ( j = i ; j < ( i + k ) ; j ++ ) { if ( a [ j ] <= a [ j + 1 ] ) count ++ ; } if ( count == k ) return 1 ; else return 0 ; } int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long int n , k , i ; int d ; long int c ; scanf ( "%ld" , & n ) ; long int a [ n ] ; c = n ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%ld" , & a [ i ] ) ; for ( k = 1 ; k <= n - 1 ; k ++ ) { for ( i = 0 ; i <= n - 2 ; i ++ ) { if ( ( i + k ) > n - 1 ) break ; d = check ( a , i , k ) ; if ( d == 1 ) c ++ ; } } printf ( "%ld\n" , c ) ; } return 0 ; }