int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int a , b ; scanf ( "%lld%lld" , & a , & b ) ; long long int i , gcd ; if ( a < b ) { for ( i = 1 ; i <= a ; i ++ ) { if ( a % i == 0 && b % i == 0 ) gcd = i ; } } else { for ( i = 1 ; i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) gcd = i ; } } printf ( "%lld %lld\n" , gcd , ( a * b ) / gcd ) ; } return 0 ; }