int main ( ) { int T ; scanf ( "%d" , & T ) ; while ( T -- ) { int N ; scanf ( "%d" , & N ) ; int a [ N ] , i ; int s = 0 ; for ( i = 0 ; i < N ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } i = 0 ; while ( i != N - 1 ) { int temp ; if ( a [ i ] < a [ i + 1 ] ) { s = s + a [ i ] ; temp = a [ i + 1 ] ; a [ i + 1 ] = a [ N - 1 ] ; a [ N - 1 ] = temp ; N = N - 1 ; } else { s = s + a [ i + 1 ] ; temp = a [ i ] ; a [ i ] = a [ N - 1 ] ; a [ N - 1 ] = temp ; N = N - 1 ; } } printf ( "%d\n" , s ) ; } return 0 ; }