int main ( ) { int n , i ; long int a , b , num , denom , gcd , lcm , rem ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%ld %ld" , & a , & b ) ; if ( a > b ) { num = a ; denom = b ; } else { num = b ; denom = a ; } rem = num % denom ; while ( rem != 0 ) { num = denom ; denom = rem ; rem = num % denom ; } gcd = denom ; lcm = ( a * b ) / gcd ; printf ( "%ld " , gcd ) ; printf ( "%ld\n" , lcm ) ; } }