int main ( ) { int i , a , b , x , y , t , test , gcd , lcm ; scanf ( "%d" , & test ) ; for ( i = 0 ; i < test ; i ++ ) { scanf ( "%d%d" , & x , & y ) ; a = x ; b = y ; while ( b != 0 ) { t = b ; b = a % b ; a = t ; } gcd = a ; lcm = ( x * y ) / gcd ; printf ( "%d " , gcd ) ; printf ( "%d\n" , lcm ) ; } return 0 ; }