int main ( ) { int t , i , g , l , a , b ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; g = gcd ( a , b ) ; l = ( a * b ) / g ; printf ( "%d %d\n" , g , l ) ; } return 0 ; } int gcd ( int a , int b ) { int g , s , i ; if ( a > b ) { g = a ; s = b ; } else { s = a ; g = b ; } for ( i = s ; i >= 1 ; i -- ) { if ( ( g % i == 0 ) && ( s % i == 0 ) ) { return i ; } } }