int main ( int argc , char * argv [ ] ) { int t ; printf ( "Enter the no of test case:\n" ) ; scanf ( "%d" , & t ) ; while ( t -- ) { int n1 , n2 , x , y ; printf ( "enter two no:\n" ) ; scanf ( "%d%d" , & n1 , & n2 ) ; x = gcd ( n1 , n2 ) ; printf ( "%d\t" , x ) ; y = lcm ( n1 , n2 ) ; printf ( "%d\n" , y ) ; } } int gcd ( int n1 , int n2 ) { while ( n1 != n2 ) { if ( n1 > n2 ) n1 = n1 - n2 ; else n2 = n2 - n1 ; } return n1 ; } int lcm ( int n1 , int n2 ) { int x , y ; x = n1 , y = n2 ; while ( n1 != n2 ) { if ( n1 > n2 ) n1 = n1 - n2 ; else n2 = n2 - n1 ; } return x * y / n1 ; }