int main ( int argc , char * argv [ ] ) { long t , i , j , n , max , temp ; scanf ( "%ld" , & t ) ; while ( t -- ) { max = 0 ; scanf ( "%ld" , & n ) ; long a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%ld" , & a [ i ] ) ; } i = 0 ; while ( i < n - 1 ) { if ( a [ i ] > a [ i + 1 ] ) { max = max + a [ i + 1 ] ; i ++ ; } else if ( a [ i ] < a [ i + 1 ] ) { max = max + a [ i ] ; temp = a [ i ] ; a [ i ] = a [ i + 1 ] ; a [ i + 1 ] = temp ; i ++ ; } } printf ( "%ld\n" , max ) ; } return 0 ; }