int main ( ) { int n , i , r , sum , j ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { sum = 0 ; scanf ( "%d" , & r ) ; int Ar [ r ] ; for ( j = 0 ; j < r ; j ++ ) { scanf ( "%d" , & Ar [ j ] ) ; } while ( r > 1 ) { if ( Ar [ 0 ] > Ar [ 1 ] ) { sum += Ar [ 1 ] ; for ( j = 0 ; j < r ; j ++ ) { Ar [ j ] = Ar [ j + 1 ] ; } r -- ; } else { sum += Ar [ 0 ] ; for ( j = 1 ; j < r ; j ++ ) { Ar [ j ] = Ar [ j + 1 ] ; } r -- ; } } printf ( "%d\n" , sum ) ; } return 0 ; }