int main ( ) { int t ; long long int a , b , lcm , gdc , i , j ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%lld%lld" , & a , & b ) ; i = 1 ; while ( a / i > 0 && b / i > 0 ) { if ( a % i == 0 && b % i == 0 ) gdc = i ; i ++ ; } j = ( a > b ) ? a : b ; while ( 1 ) { if ( j % a == 0 && j % b == 0 ) { lcm = j ; break ; } j ++ ; } printf ( "%lld %lld\n" , gdc , lcm ) ; } return 0 ; }