int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n ; scanf ( "%d" , & n ) ; long long int a [ n ] ; for ( int i = 0 ; i < n ; i ++ ) scanf ( "%lld" , & a [ i ] ) ; long long int ans = 0 ; for ( int i = 0 ; i < n ; i ++ ) { ans += 1 ; for ( int j = i ; j < n - 1 ; j ++ ) { if ( a [ j ] <= a [ j + 1 ] ) ans ++ ; else break ; } } printf ( "%lld\n" , ans ) ; } return 0 ; }