unsigned int calgcd ( unsigned int a , unsigned int b ) { unsigned int temp ; while ( b != 0 ) { temp = b ; b = a % b ; a = temp ; } return a ; } int main ( ) { unsigned int a , b , gcd ; int t ; long unsigned int lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%u%u" , & a , & b ) ; gcd = calgcd ( a , b ) ; lcm = a * b / gcd ; printf ( "%u,%lu\n" , gcd , lcm ) ; } return 0 ; }