int a [ 100 ] [ 100 ] ; long int N ; long int greatest ( int row , int column ) { long int down , bottom ; if ( row == N - 1 ) return a [ row ] [ column ] ; else { down = greatest ( row + 1 , column ) ; bottom = greatest ( row + 1 , column + 1 ) ; if ( down >= bottom ) return a [ row ] [ column ] + down ; else return a [ row ] [ column ] + bottom ; } } int main ( ) { int T , i , j ; long int sum ; scanf ( "%d" , & T ) ; while ( T -- ) { sum = 0 ; scanf ( "%d" , & N ) ; for ( i = 0 ; i < N ; i ++ ) for ( j = 0 ; j <= i ; j ++ ) scanf ( "%d" , & a [ i ] [ j ] ) ; sum = greatest ( 0 , 0 ) ; printf ( "%d\n" , sum ) ; } return 0 ; }