void lcm ( long int a , long int b ) { static long long int i , m , k , s ; if ( a > b ) m = a ; else m = b ; s = m ; while ( 1 ) { if ( m % a == 0 && m % b == 0 ) { break ; } m ++ ; } for ( i = 1 ; i <= s ; i ++ ) { if ( a % i == 0 && b % i == 0 ) k = i ; } printf ( "%lld %lld\n" , k , m ) ; } int main ( ) { int t ; long int a , b ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%ld%ld" , & a , & b ) ; lcm ( a , b ) ; } return 0 ; }