int main ( ) { int a , b , n , repeat ; int temp ; int gcd , lcm , tempa , tempb ; scanf ( "%d" , & n ) ; for ( repeat = 0 ; repeat < n ; repeat ++ ) { scanf ( "%d%d" , & a , & b ) ; if ( a < b ) { tempa = b ; tempb = a ; } else { tempa = a ; tempb = b ; } temp = a ; while ( b != 0 ) { a = b ; b = temp % b ; temp = a ; } gcd = a ; printf ( "%d" , a ) ; a = tempa / gcd ; b = tempb / gcd ; lcm = gcd * a * b ; printf ( " %d" , lcm ) ; printf ( "\n" ) ; } return 0 ; }