int arr [ 100 ] = { 0 } ; int main ( ) { int i , n , j = 0 , sum , k , flag = 0 , m ; scanf ( "%d" , & m ) ; while ( m -- ) { sum = 0 ; flag = 0 ; j = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { for ( k = 0 ; k <= i ; k ++ ) { scanf ( "%d" , & arr [ k ] ) ; } if ( flag == 1 ) { j = ( arr [ j ] > arr [ j + 1 ] ? ( ( arr [ j ] > arr [ j + 2 ] ? j : j + 2 ) ) : ( ( arr [ j + 1 ] > arr [ j + 2 ] ) ? j + 1 : j + 2 ) ) ; sum = sum + arr [ j ] ; } else { if ( i == 0 ) { sum = sum + arr [ j ] ; } else if ( arr [ j + 1 ] > arr [ j ] ) { j ++ ; sum = sum + arr [ j ] ; flag = 0 ; } else if ( arr [ j + 1 ] < arr [ j ] ) { sum = sum + arr [ j ] ; flag = 0 ; } else { sum = sum + arr [ j ] ; flag = 1 ; } } } printf ( "%d" , sum ) ; } return 0 ; }