int main ( ) { int T ; scanf ( "%d" , & T ) ; while ( T -- ) { int a , b ; scanf ( "%d %d" , & a , & b ) ; int rem , num , denom ; if ( a > b ) { num = a ; denom = b ; } else { num = b ; denom = a ; } rem = num % denom ; while ( rem != 0 ) { num = denom ; denom = rem ; rem = num % denom ; } int gcd = denom ; int lcm = ( ( a * b ) / gcd ) ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }