int main ( ) { int i , k , x , n , t , sum , a [ 49999 ] ; scanf ( "%d" , & t ) ; for ( x = 0 ; x <= t ; x ++ ) { sum = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < n - 1 ; n -- ) { if ( a [ i ] >= a [ i + 1 ] ) { sum = sum + a [ i + 1 ] ; for ( k = i ; k < n ; k ++ ) { a [ k ] = a [ k + 1 ] ; } } else { sum = sum + a [ i ] ; for ( k = i + 1 ; k < n ; k ++ ) { a [ k ] = a [ k + 1 ] ; } } } printf ( "%d\n" , sum ) ; } return ; }