int main ( ) { int t , n , i , s , f ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; f = a [ 0 ] ; s = a [ 1 ] ; if ( n == 2 ) { if ( s > f ) s = f ; } else { for ( i = 2 ; i < n ; i ++ ) { if ( a [ i ] > s ) s = a [ i ] ; if ( s > f ) { s = s + f ; f = s - f ; s = s - f ; } } } printf ( "%d\n" , s ) ; } }