int subinc ( ) { int n ; scanf ( "%d" , & n ) ; int arr [ n ] ; int i = 0 , j = 0 , count = n ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & arr [ i ] ) ; } i = 0 ; for ( i = 0 ; i < n - 1 ; i ++ ) { j = 0 ; while ( arr [ i + j ] <= arr [ i + j + 1 ] && ( i + j + 1 ) < n ) { j ++ ; } count += j ; } return count ; } int main ( ) { int t ; scanf ( "%d" , & t ) ; int i ; for ( i = 1 ; i <= t ; i ++ ) { printf ( "%d" , subinc ( ) ) ; } return 0 ; }