int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b ; scanf ( "%d%d" , & a , & b ) ; int gcd = ( a < b ) ? a : b ; int lcm = ( a > b ) ? a : b ; for ( int i = gcd ; i > 0 ; i -- ) { if ( ( a % i == 0 ) && ( b % i == 0 ) ) { gcd = i ; break ; } } for ( int i = lcm ; i < 1000000 ; i ++ ) { if ( ( i % a == 0 ) && ( i % b == 0 ) ) { lcm = i ; break ; } } printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }