int main ( ) { int t ; scanf ( "%d" , & t ) ; long long int n1 , n2 , remainder , numerator , denominator , lcm , gcd ; while ( t -- ) { lcm = 0 , gcd = 0 , denominator = 0 ; scanf ( "%lld%lldd" , & n1 , & n2 ) ; if ( n1 > n2 ) { numerator = n1 ; denominator = n2 ; } else { numerator = n2 ; denominator = n1 ; } remainder = numerator % denominator ; while ( remainder != 0 ) { numerator = denominator ; denominator = remainder ; remainder = numerator % denominator ; } gcd = denominator ; lcm = n1 * n2 / gcd ; printf ( "%lld %lld\n" , gcd , lcm ) ; } }