int main ( ) { int t , i , n , j , s , k ; scanf ( "%d" , & t ) ; int m [ t ] , p [ t ] ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; int a [ n ] ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%d" , & a [ j ] ) ; for ( j = 1 ; j < n ; j ++ ) { if ( a [ 0 ] > a [ j ] ) { s = a [ 0 ] ; a [ 0 ] = a [ j ] ; a [ j ] = s ; } } m [ i ] = a [ 0 ] ; p [ i ] = n ; } for ( k = 0 ; k < t ; k ++ ) printf ( "%d\n" , m [ k ] * ( p [ k ] - 1 ) ) ; return 0 ; }