int maxim ( int a , int b ) { if ( a > b ) return a ; else if ( b > a ) return b ; else return a ; } int main ( void ) { long long int tests ; scanf ( "%lld" , & tests ) ; long long int range ; for ( range = 0 ; range < tests ; range ++ ) { long long int num ; scanf ( "%lld" , & num ) ; long long int i , j ; long long int arr [ num ] [ num ] ; for ( i = 0 ; i < num ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%lld" , & arr [ i ] [ j ] ) ; } } long long int k , k1 , val , lol ; long long int a , b ; for ( k = num - 1 ; k > 0 ; k -- ) { for ( k1 = k - 1 ; k1 >= 0 ; k1 -- ) { a = arr [ k ] [ k1 ] ; b = arr [ k ] [ k1 + 1 ] ; val = arr [ k - 1 ] [ k1 ] ; lol = maxim ( a , b ) ; arr [ k - 1 ] [ k1 ] = lol + val ; } } printf ( "%lld\n" , arr [ 0 ] [ 0 ] ) ; } return 0 ; }