main ( ) { long int a [ 100000 ] , t , i , j , n , c , h , p , k ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%ld" , & n ) ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%ld" , & a [ j ] ) ; c = n ; for ( j = 0 ; j < n ; j ++ ) { p = 0 ; for ( k = j + 1 ; k < n ; k ++ ) { if ( a [ k ] >= a [ k - 1 ] ) { p ++ ; j = k ; } else break ; } for ( h = 1 ; h <= p ; h ++ ) c = c + h ; } printf ( "%ld\n" , c ) ; } return 0 ; }