long long int GCD ( long long int x , long long int y ) { if ( x == 0 ) return y ; while ( y != 0 ) { if ( x > y ) x = x - y ; else y = y - x ; } return x ; } int main ( ) { int t , i ; long long int a , b ; long long int ans1 , ans2 ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lld%lld" , & a , & b ) ; ans1 = GCD ( a , b ) ; ans2 = ( a * b ) / ans1 ; printf ( "%lld %lld\n" , ans1 , ans2 ) ; } return 0 ; }