int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t != 0 ) { int n ; scanf ( "%d" , & n ) ; int arr [ n ] , i , j , count = 0 , list ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%d" , & arr [ j ] ) ; } for ( i = 0 ; i < n - 1 ; i ++ ) { if ( arr [ i ] > arr [ i + 1 ] ) { count += arr [ i + 1 ] ; } else { list = arr [ i ] ; arr [ i ] = arr [ i + 1 ] ; arr [ i + 1 ] = list ; count += arr [ i + 1 ] ; } } printf ( "%d" , count ) ; } t -- ; return 0 ; }