main ( ) { int p , i , b , a , j , d , n , k , l , x , y , max1 ; scanf ( "%d" , & a ) ; for ( i = 1 ; i <= a ; i ++ ) { scanf ( "%d" , & n ) ; int c [ n + 1 ] [ n + 1 ] ; int max [ n + 2 ] ; for ( j = 0 ; j <= n ; j ++ ) { for ( k = 0 ; k <= n ; k ++ ) { c [ j ] [ k ] = 0 ; max [ k ] = 0 ; } } max [ n + 1 ] = 0 ; for ( j = 1 ; j <= n ; j ++ ) { for ( k = 1 ; k <= j ; k ++ ) { scanf ( "%d" , & c [ j ] [ k ] ) ; } } p = 0 ; max [ 1 ] = c [ 1 ] [ 1 ] ; for ( j = 2 ; j <= n ; j ++ ) { for ( k = 1 ; k <= j ; k ++ ) { x = c [ j ] [ k ] + p ; y = c [ j ] [ k ] + max [ k ] ; p = max [ k ] ; max [ k ] = ( ( x > y ) ? x : y ) ; } } max1 = max [ 1 ] ; for ( k = 2 ; k <= n ; k ++ ) { max1 = ( ( max1 > max [ k ] ) ? max1 : max [ k ] ) ; } printf ( "%d" , max1 ) ; } }