int main ( void ) { int t , k = 0 , i ; long a [ 100000 ] , n , sum = 0 , first ; scanf ( "%d" , & t ) ; while ( k < t ) { sum = 0 ; scanf ( "%ld" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%ld" , & a [ i ] ) ; first = a [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ i ] < first ) { sum = sum + a [ i ] ; first = a [ i ] ; } else sum = sum + first ; } printf ( "%ld\n" , sum ) ; k ++ ; } return 0 ; }