int main ( void ) { int t , i , j , k , n , count ; long long * a ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; count = n ; a = ( long long * ) calloc ( n , sizeof ( long long ) ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%lld" , & a [ i ] ) ; for ( i = 0 ; i < n - 1 ; i ++ ) { k = i + 1 ; j = i ; while ( a [ j ] < a [ k ] && k < n ) { count ++ ; j ++ ; k ++ ; } } printf ( "%d\n" , count ) ; } return 0 ; }