int main ( ) { int N , i = 1 ; int a , b , lcm , hcf , least , greatest ; scanf ( " %d" , & N ) ; while ( N -- ) { scanf ( " %d %d" , & a , & b ) ; least = ( a < b ) ? a : b ; greatest = ( least == a ) ? b : a ; for ( i = 1 ; i <= least ; i ++ ) if ( a % i == 0 && b % i == 0 ) hcf = i ; for ( i = 1 ; ; i ++ ) { if ( ( greatest * i ) % least == 0 ) { lcm = greatest ; break ; } } printf ( "%d %d" , hcf , lcm ) ; } return 0 ; }