int main ( void ) { int t ; int n ; int i ; int j ; int max = 0 ; int max_j ; int x ; int y ; int c = 0 ; int a [ 101 ] [ 101 ] ; scanf ( "%d" , & t ) ; while ( t -- ) { c = 0 ; max = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } } for ( i = n - 1 ; i >= 0 ; i -- ) { if ( a [ n - 1 ] [ i ] > max ) { max = a [ n - 1 ] [ i ] ; max_j = i ; } } c += max ; for ( i = n - 2 ; i >= 0 ; i -- ) { x = 0 ; y = 0 ; if ( max_j < i + 1 ) { x = a [ i ] [ max_j ] ; } if ( max_j > 0 ) { y = a [ i ] [ max_j - 1 ] ; } if ( x > y ) { c += x ; } else { c += y ; max_j -- ; } } printf ( "%d\n" , c ) ; } return 0 ; }