main ( ) { int num , i , count , k ; scanf ( "%d" , & count ) ; while ( count -- ) { scanf ( "%d" , & num ) ; k = num * ( num + 1 ) / 2 ; int a [ k ] ; for ( i = 0 ; i < k ; i ++ ) scanf ( "%d" , & a [ i ] ) ; k = k - num ; while ( -- num ) { k = k - num ; a [ k ] += a [ k + num ] > a [ k + num + 1 ] ? a [ k + num ] : a [ k + num + 1 ] ; } printf ( "%d\n" , a [ 0 ] ) ; } return 0 ; }