int main ( void ) { int t , n ; scanf ( "%d" , & t ) ; while ( t -- > 0 ) { scanf ( "%d" , & n ) ; int * a [ n ] ; int i , j , temp , ans ; ans = 0 ; for ( i = 0 ; i < n ; i ++ ) { a [ i ] = ( int * ) malloc ( ( i + 1 ) * sizeof ( int ) ) ; } for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & temp ) ; if ( i == 0 ) { a [ i ] [ j ] = temp ; ans = MAX ( ans , a [ i ] [ j ] ) ; } else if ( i != j && j != 0 ) { a [ i ] [ j ] = MAX ( temp + a [ i - 1 ] [ j ] , temp + a [ i - 1 ] [ j - 1 ] ) ; ans = MAX ( ans , a [ i ] [ j ] ) ; } else if ( j == 0 ) { a [ i ] [ j ] = a [ i - 1 ] [ j ] + temp ; ans = MAX ( ans , a [ i ] [ j ] ) ; } else if ( i == j ) { a [ i ] [ j ] = a [ i - 1 ] [ j - 1 ] + temp ; ans = MAX ( ans , a [ i ] [ j ] ) ; } } } printf ( "%d\n" , ans ) ; ans = 0 ; } return 0 ; }