int main ( ) { long long int t , n , sum , i , j , c ; scanf ( "%lld" , & t ) ; while ( t -- ) { scanf ( "%lld" , & n ) ; long long int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & a [ i ] ) ; } c = 1 ; sum = 0 ; for ( i = 0 ; i < n ; i ++ ) { if ( a [ i ] < a [ i + 1 ] && i != n - 1 ) { c ++ ; } else { sum += c * ( c + 1 ) / 2 ; c = 1 ; } } printf ( "%lld\n" , sum ) ; } }