int gcd ( int a , int b ) { int i , hcf = 1 ; for ( i = 1 ; i <= a || i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) hcf = i ; } return hcf ; } int lcm ( int a , int b ) { int l ; l = ( a * b ) / ( gcd ( a , b ) ) ; return l ; } int main ( ) { int t , j , a , b , hcf , lc ; scanf ( "%d" , & t ) ; for ( j = 0 ; j < t ; j ++ ) { scanf ( "%d%d" , & a , & b ) ; hcf = gcd ( a , b ) ; lc = lcm ( a , b ) ; printf ( "%d %d\n" , hcf , lc ) ; } return 0 ; }