long gcd ( long a , long b ) { long i , ans = 1 ; } int main ( ) { int t , i , j ; long a , b , hcf , lcm , ans = 1 , smaller ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%ld %ld" , & a , & b ) ; if ( a < b ) smaller = a ; else smaller = b ; if ( a == b ) hcf = a ; else { ans = 1 ; for ( j = 2 ; j <= smaller ; j ++ ) { if ( a % j == 0 && b % j == 0 ) { ans = j ; continue ; } } hcf = ans ; } lcm = ( a * b ) / hcf ; printf ( "%ld %ld\n" , hcf , lcm ) ; } }